mapper.select:
SELECT FROM NIMA_EMPLOYEE_INFO E LEFT JOIN NIMA_DEPT_INFO D ON E.DEPT_ID = D.ID LEFT JOIN NIMA_JOB_INFO J ON E.JOB_ID = J.ID WHERE 1 = 1 AND E.DEL_FLAG = 0
AND E.JOB_ID = #{employeeInfo.jobId,jdbcType=DECIMAL}
AND E.NAME LIKE concat(concat('%',#{employeeInfo.name,jdbcType=VARCHAR}),'%')
AND E.CARD_ID LIKE concat(concat('%',#{employeeInfo.cardId,jdbcType=VARCHAR}),'%')
AND E.SEX = #{employeeInfo.sex,jdbcType=DECIMAL}
AND E.TEL LIKE concat(concat('%',#{employeeInfo.tel,jdbcType=VARCHAR}),'%')
AND E.DEPT_ID = #{employeeInfo.deptId,jdbcType=DECIMAL}
ORDER BY E.ID DESC

   SELECT 
     FROM NIMA_EMPLOYEE_INFO E
LEFT JOIN NIMA_DEPT_INFO D
       ON E.DEPT_ID = D.ID
LEFT JOIN NIMA_JOB_INFO J
       ON E.JOB_ID = J.ID


      AND E.DEPT_ID = #{deptId,jdbcType=DECIMAL}


      AND E.JOB_ID = #{jobId,jdbcType=DECIMAL}


      AND E.NAME = #{name,jdbcType=VARCHAR}


      AND E.CARD_ID = #{cardId,jdbcType=VARCHAR}


