      AND E.POST_CODE = #{postCode,jdbcType=VARCHAR}


      AND E.TEL = #{tel,jdbcType=VARCHAR}


      AND E.QQ_NUM = #{qqNum,jdbcType=VARCHAR}


      AND E.EMAIL = #{email,jdbcType=VARCHAR}


      AND E.SEX = #{sex,jdbcType=DECIMAL}


      AND E.PARTY = #{party,jdbcType=VARCHAR}


      AND E.BIRTHDAY = #{birthday,jdbcType=TIMESTAMP}


      AND E.RACE = #{race,jdbcType=VARCHAR}


